// Gate: 1m (high) / 60m (normal) — Generic (DHT or XY-MD02), no Telegram

// --- config (ENV + per-item overrides) ---
const HIGH_EVERY_MIN_DEFAULT   = Number($env.HIGH_EVERY_MIN   ?? 1);
const NORMAL_EVERY_MIN_DEFAULT = Number($env.NORMAL_EVERY_MIN ?? 60);

const item  = { ...$json };
const nowMs = Date.now();

// --- identity (unique key per device/stream) ---
const siteName  = item.site || 'default';
const baseTopic = item.baseTopic || item.topic || 'XYMD02'; // เดิม DHTsensor → ปรับ fallback เป็น XYMD02
const sensorId  = item.sensor_id ?? item.slave_id ?? item.device_id ?? null;
const sensorKey = sensorId ? `${siteName}::${baseTopic}::${sensorId}` : `${siteName}::${baseTopic}`;

// --- per-item overrides for periods ---
const highEveryMin   = Number.isFinite(Number(item.high_every_min))   ? Number(item.high_every_min)   : HIGH_EVERY_MIN_DEFAULT;
const normalEveryMin = Number.isFinite(Number(item.normal_every_min)) ? Number(item.normal_every_min) : NORMAL_EVERY_MIN_DEFAULT;

// --- global state ---
const g = getWorkflowStaticData('global');
g._gate      = g._gate      || {};
g._lastValid = g._lastValid || {};

g._gate[sensorKey] = g._gate[sensorKey] || { lastMode: 'normal', lastHighAt: 0, lastNormalAt: 0, initDone: false };
g._lastValid[sensorKey] = g._lastValid[sensorKey] || { temp: null, hum: null };

// --- helpers ---
const hasNum = (v) => {
  if (v === '' || v === null || v === undefined) return false;
  const n = Number(v);
  return Number.isFinite(n);
};
const pick = (cur, last) => (hasNum(cur) ? Number(cur) : (hasNum(last) ? Number(last) : null));

// --- track last valid values ---
if (hasNum(item.temp)) g._lastValid[sensorKey].temp = Number(item.temp);
if (hasNum(item.hum))  g._lastValid[sensorKey].hum  = Number(item.hum);

// --- fallback to last valid if missing this tick ---
item.temp = pick(item.temp, g._lastValid[sensorKey].temp);
item.hum  = pick(item.hum,  g._lastValid[sensorKey].hum);
item.value_source = (hasNum($json.temp) || hasNum($json.hum)) ? 'current' : 'last';

// --- threshold & mode ---
const thresholdTemp = hasNum(item.threshold_temp)
  ? Number(item.threshold_temp)
  : (hasNum($env.TEMP_THRESHOLD) ? Number($env.TEMP_THRESHOLD) : 35);

let modeNow = (item.mode === 'high' || item.mode === 'normal') ? item.mode : 'normal';
if (hasNum(item.temp)) modeNow = (Number(item.temp) > thresholdTemp) ? 'high' : 'normal';

const prevMode = g._gate[sensorKey].lastMode;

// --- decide logging frequency ---
let shouldLog     = false;
let status_detail = 'normal_periodic';

// ยิง log แรกสุดของอุปกรณ์นี้ทันที (init) เพื่อไม่ต้องรอครบช่วงเวลา
if (!g._gate[sensorKey].initDone) {
  shouldLog = true;
  status_detail = 'init';
  if (modeNow === 'high') {
    g._gate[sensorKey].lastHighAt = nowMs;
  } else {
    g._gate[sensorKey].lastNormalAt = nowMs;
  }
  g._gate[sensorKey].initDone = true;
} else if (modeNow === 'high') {
  const due = nowMs >= g._gate[sensorKey].lastHighAt + highEveryMin * 60 * 1000;
  if (due) {
    shouldLog = true;
    g._gate[sensorKey].lastHighAt = nowMs;
  }
  status_detail = 'high';
} else {
  if (prevMode === 'high') {
    // recovery: high -> normal บันทึกทันทีหนึ่งครั้ง
    shouldLog = true;
    status_detail = 'recovery';
    g._gate[sensorKey].lastNormalAt = nowMs;
  } else {
    const due = nowMs >= g._gate[sensorKey].lastNormalAt + normalEveryMin * 60 * 1000;
    if (due) {
      shouldLog = true;
      g._gate[sensorKey].lastNormalAt = nowMs;
    }
    status_detail = 'normal_periodic';
  }
}

// update last mode
g._gate[sensorKey].lastMode = modeNow;

// --- enrich fields for downstream (GS, etc.) ---
item.mode             = modeNow;
item.threshold_temp   = thresholdTemp;
item.status_detail    = status_detail;
item.shouldLog        = !!shouldLog;
item.gate_key         = sensorKey;
item.high_every_min   = highEveryMin;
item.normal_every_min = normalEveryMin;

return [item];

// Parse EnvSensor (normalize) — works with DHT & XY-MD02
// Output columns: ts, site, temp, tunit, hum, hunit, threshold_temp, mode, isHigh, isLow, status_detail, baseTopic

const SITE           = $json.site ?? $json.site_name ?? $env.SITE_NAME ?? "MSU_Lab";
const BASE_TOPIC     = $json.baseTopic ?? $json.topic ?? $env.MQTT_BASE_TOPIC ?? "XYMD02";
// ใช้ threshold จาก JSON ก่อน แล้วค่อย fallback ไป ENV (ตามที่ตั้งใจ)
const INPUT_THRESHOLD = Number($json.threshold_temp ?? $env.TEMP_THRESHOLD ?? 35);
const TZ_OFFSET_MIN  = Number($env.TZ_OFFSET_MIN ?? 420); // Asia/Bangkok

// ---- รับ payload จาก MQTT Trigger ($json.message เป็น object หรือ string) ----
let payload = $json.message ?? $json;
if (typeof payload === 'string') { try { payload = JSON.parse(payload); } catch { payload = {}; } }

// ---- helpers ----
const toNum = (v) => {
  if (v === undefined || v === null) return null;
  if (typeof v === 'string' && v.trim() === '') return null;
  const n = Number(v);
  return Number.isFinite(n) ? n : null;
};
// เดาสเกล x10 สำหรับค่าดิบ register (เช่น 275 -> 27.5)
const maybeScale10 = (n) => (n !== null && Number.isInteger(n) && Math.abs(n) >= 100) ? n / 10 : n;
const pickFirstNum = (...cands) => {
  for (const c of cands) { const n = toNum(c); if (n !== null) return n; }
  return null;
};

// ---- ดึงค่า temp/hum รองรับหลายรูปแบบ (DHT & Modbus) ----
// DHT-style fields
let temp = pickFirstNum(
  payload.temp, payload.temperature, payload.dht?.temp,
  payload.DHT11?.temp, payload.DHT22?.temperature, payload.temp_c, payload.tC, payload.T
);
let hum  = pickFirstNum(
  payload.hum, payload.humidity, payload.dht?.hum,
  payload.DHT11?.humidity, payload.DHT22?.hum, payload.rh, payload.RH
);

// Modbus raw (x10)
if (temp === null) temp = maybeScale10(toNum(payload.temp_raw));
if (hum  === null) hum  = maybeScale10(toNum(payload.hum_raw));

// Modbus registers: { registers:[t_x10, h_x10] } หรือ object {r0,r1}/{"0","1"}
if ((temp === null || hum === null) && payload.registers) {
  const r = payload.registers;
  const r0 = Array.isArray(r) ? r[0] : (r['0'] ?? r.r0 ?? r.reg0);
  const r1 = Array.isArray(r) ? r[1] : (r['1'] ?? r.r1 ?? r.reg1);
  if (temp === null) temp = maybeScale10(toNum(r0));
  if (hum  === null) hum  = maybeScale10(toNum(r1));
}

// ซ้อนใน data: { data:{ temp:..., hum:... } }
if ((temp === null || hum === null) && payload.data && typeof payload.data === 'object') {
  if (temp === null) temp = maybeScale10(pickFirstNum(payload.data.temp, payload.data.temperature, payload.data.temp_raw));
  if (hum  === null) hum  = maybeScale10(pickFirstNum(payload.data.hum, payload.data.humidity, payload.data.hum_raw, payload.data.rh));
}

// ---- คำนวณสถานะจาก threshold ----
const hasThreshold = Number.isFinite(INPUT_THRESHOLD);
const isHigh = hasThreshold && temp !== null && temp > INPUT_THRESHOLD;
const mode   = isHigh ? 'high' : 'normal';

// ---- เวลาโลคัล "YYYY-MM-DD HH:mm:ss" ----
const ts = new Date(Date.now() + TZ_OFFSET_MIN * 60 * 1000)
  .toISOString().replace('T',' ').slice(0,19);

// ---- ข้อความสั้นเพื่อ debug ----
const status_detail = (() => {
  if (temp === null) return 'temp=N/A';
  if (!hasThreshold) return `TEMP ${temp}°C (no TH)`;
  return isHigh
    ? `TEMP ${temp}°C > TH ${INPUT_THRESHOLD}°C`
    : `TEMP ${temp}°C ≤ TH ${INPUT_THRESHOLD}°C`;
})();

return [{
  ts,
  site: SITE,
  temp,  tunit: $json.tunit || '°C',
  hum,   hunit: $json.hunit || '%',
  threshold_temp: hasThreshold ? INPUT_THRESHOLD : null,
  mode,
  isHigh: isHigh ? 1 : 0,
  isLow : (hasThreshold && temp !== null && temp < INPUT_THRESHOLD) ? 1 : 0,
  status_detail,
  baseTopic: BASE_TOPIC,
}];
