// Gate: 1m (high) / 60m (normal) â€” DHT only + Telegram flags
const HIGH_EVERY_MIN    = Number($env.HIGH_EVERY_MIN ?? 1);    // high: à¸—à¸¸à¸ X à¸™à¸²à¸—à¸µ
const NORMAL_EVERY_MIN  = Number($env.NORMAL_EVERY_MIN ?? 60); // normal: à¸—à¸¸à¸ X à¸™à¸²à¸—à¸µ

const item  = { ...$json };
const nowMs = Date.now();

const siteName  = item.site || 'default';
const baseTopic = item.baseTopic || 'DHTsensor';
const sensorKey = `${siteName}::${baseTopic}`;

const g = getWorkflowStaticData('global');

// --- state ---
g._gate      = g._gate      || {};
g._lastValid = g._lastValid || {};
g._gate[sensorKey] = g._gate[sensorKey] || { lastMode: 'normal', lastHighAt: 0, lastNormalAt: 0 };
g._lastValid[sensorKey] = g._lastValid[sensorKey] || { temp: null, hum: null };

// --- helpers ---
const hasNum = v => Number.isFinite(Number(v));
const pick   = (cur, last) => (hasNum(cur) ? Number(cur) : (hasNum(last) ? last : null));
const f1     = v => (hasNum(v) ? Number(v).toFixed(1) : 'â€”');

// --- last valid ---
if (hasNum(item.temp)) g._lastValid[sensorKey].temp = Number(item.temp);
if (hasNum(item.hum))  g._lastValid[sensorKey].hum  = Number(item.hum);

// --- fallback ---
item.temp = pick(item.temp, g._lastValid[sensorKey].temp);
item.hum  = pick(item.hum,  g._lastValid[sensorKey].hum);
item.value_source = (hasNum($json.temp) || hasNum($json.hum)) ? 'current' : 'last';

// --- threshold & mode ---
const thresholdTemp = hasNum(item.threshold_temp)
  ? Number(item.threshold_temp)
  : (hasNum($env.TEMP_THRESHOLD) ? Number($env.TEMP_THRESHOLD) : 35);

let modeNow = (item.mode === 'high' || item.mode === 'normal') ? item.mode : 'normal';
if (hasNum(item.temp)) modeNow = (Number(item.temp) > thresholdTemp) ? 'high' : 'normal';

const prevMode = g._gate[sensorKey].lastMode;

// --- decide frequency (log & notify) ---
let shouldLog = false;
let shouldNotify = false;
let status_detail = 'normal_periodic';

if (modeNow === 'high') {
  const due = nowMs >= g._gate[sensorKey].lastHighAt + HIGH_EVERY_MIN * 60 * 1000;
  if (due) {
    shouldLog = true;
    shouldNotify = true;
    g._gate[sensorKey].lastHighAt = nowMs;
  }
  status_detail = 'high';
} else {
  if (prevMode === 'high') {
    // recovery: à¹à¸ˆà¹‰à¸‡ & à¸šà¸±à¸™à¸—à¸¶à¸à¸—à¸±à¸™à¸—à¸µ 1 à¸„à¸£à¸±à¹‰à¸‡
    shouldLog = true;
    shouldNotify = true;
    status_detail = 'recovery';
    g._gate[sensorKey].lastNormalAt = nowMs;
  } else {
    const due = nowMs >= g._gate[sensorKey].lastNormalAt + NORMAL_EVERY_MIN * 60 * 1000;
    if (due) {
      shouldLog = true;
      shouldNotify = true;
      g._gate[sensorKey].lastNormalAt = nowMs;
    }
    status_detail = 'normal_periodic';
  }
}
g._gate[sensorKey].lastMode = modeNow;

// --- build Telegram text (plain text) ---
const statusCategory =
  status_detail === 'high'     ? 'high' :
  status_detail === 'recovery' ? 'recovery' : 'normal';

const ICONS = { high: 'ðŸ”¥', recovery: 'ðŸŸ¢', normal: 'âœ…' };
const STATUS_TEXT = { high: 'à¸ªà¸¹à¸‡à¹€à¸à¸´à¸™à¹€à¸à¸“à¸‘à¹Œ', recovery: 'à¸Ÿà¸·à¹‰à¸™à¸•à¸±à¸§', normal: 'à¸›à¸à¸•à¸´' };

const tUnit = item.tunit || 'Â°C';
const hUnit = item.hunit || '%';
const thText = hasNum(thresholdTemp) ? ` (TH ${thresholdTemp}${tUnit})` : '';

const lines = [
  `${ICONS[statusCategory]} DHT @ ${siteName}`,
  `ðŸŒ¡ï¸ Temperature: ${f1(item.temp)} ${tUnit}${thText}`,
  `ðŸ’§ Humidity: ${f1(item.hum)} ${hUnit}`,
  `ðŸ“Ÿ Status: ${STATUS_TEXT[statusCategory]} (${statusCategory}) | ðŸ•’ Time: ${item.ts}`,
];
item.text = lines.join('\n');

// --- outputs ---
item.mode           = modeNow;
item.threshold_temp = thresholdTemp;
item.status_detail  = status_detail;
item.shouldLog      = !!shouldLog;     // à¹ƒà¸Šà¹‰à¸à¸±à¸š IF â†’ Sheets
item.shouldNotify   = !!shouldNotify;  // à¹ƒà¸Šà¹‰à¸à¸±à¸š IF â†’ Telegram

return [item];

// Parse DHT (normalize) â€” DHT only
const SITE          = $json.site ?? $json.site_name ?? $env.SITE_NAME ?? "MSU_Lab";
const BASE_TOPIC    = $json.baseTopic ?? $json.topic ?? $env.MQTT_BASE_TOPIC ?? "DHTsensor";
// â¬‡ï¸ à¹€à¸›à¸¥à¸µà¹ˆà¸¢à¸™à¸šà¸£à¸£à¸—à¸±à¸”à¸™à¸µà¹‰
const INPUT_THRESHOLD = Number($json.threshold_temp ?? $env.TEMP_THRESHOLD ?? 35);

const TZ_OFFSET_MIN = Number($env.TZ_OFFSET_MIN ?? 420);

// à¸£à¸±à¸š payload...
let payload = $json.message ?? $json;
if (typeof payload === 'string') { try { payload = JSON.parse(payload); } catch { payload = {}; } }

const toNum = (v) => {
  if (v === undefined || v === null) return null;
  if (typeof v === 'string' && v.trim() === '') return null;
  const n = Number(v);
  return Number.isFinite(n) ? n : null;
};

const temp = toNum(payload.temp ?? payload.temperature ?? payload.dht?.temp ?? payload.DHT11?.temp ?? payload.DHT22?.temperature);
const hum  = toNum(payload.hum  ?? payload.humidity  ?? payload.dht?.hum  ?? payload.DHT11?.humidity ?? payload.DHT22?.hum);

// à¸„à¸³à¸™à¸§à¸“à¸ªà¸–à¸²à¸™à¸°à¸ˆà¸²à¸ threshold à¸—à¸µà¹ˆà¹„à¸”à¹‰à¸ˆà¸²à¸ Set node (à¸«à¸£à¸·à¸­ ENV)
const hasThreshold = Number.isFinite(INPUT_THRESHOLD);
const isHigh = hasThreshold && temp !== null && temp > INPUT_THRESHOLD;
const mode   = isHigh ? 'high' : 'normal';

const ts = new Date(Date.now() + Number($env.TZ_OFFSET_MIN ?? 420) * 60 * 1000)
  .toISOString().replace('T',' ').slice(0,19);

const status_detail = (() => {
  if (temp === null) return 'temp=N/A';
  if (!hasThreshold) return `TEMP ${temp}Â°C (no TH)`;
  return isHigh
    ? `TEMP ${temp}Â°C > TH ${INPUT_THRESHOLD}Â°C`
    : `TEMP ${temp}Â°C â‰¤ TH ${INPUT_THRESHOLD}Â°C`;
})();

return [{
  ts,
  site: SITE,
  temp,  tunit: $json.tunit || 'Â°C',
  hum,   hunit: $json.hunit || '%',
  threshold_temp: hasThreshold ? INPUT_THRESHOLD : null,
  mode,
  isHigh: isHigh ? 1 : 0,
  isLow : (hasThreshold && temp !== null && temp < INPUT_THRESHOLD) ? 1 : 0,
  status_detail,
  baseTopic: BASE_TOPIC,
}];
