// Gate: 1m (high) / 60m (normal) ‚Äî DHT only + Telegram flags
//const HIGH_EVERY_MIN    = Number($env.HIGH_EVERY_MIN ?? 1);    // high: ‡∏ó‡∏∏‡∏Å X ‡∏ô‡∏≤‡∏ó‡∏µ
//const NORMAL_EVERY_MIN  = Number($env.NORMAL_EVERY_MIN ?? 60); // normal: ‡∏ó‡∏∏‡∏Å X ‡∏ô‡∏≤‡∏ó‡∏µ
const HIGH_EVERY_MIN   = Number($json.HIGH_EVERY_MIN   ?? $env.HIGH_EVERY_MIN   ?? 1);
const NORMAL_EVERY_MIN = Number($json.NORMAL_EVERY_MIN ?? $env.NORMAL_EVERY_MIN ?? 60);


const item  = { ...$json };
const nowMs = Date.now();

const siteName  = item.site || 'default';
const baseTopic = item.baseTopic || 'DHTsensor';
const sensorKey = `${siteName}::${baseTopic}`;

const g = getWorkflowStaticData('global');

// --- state ---
g._gate      = g._gate      || {};
g._lastValid = g._lastValid || {};
g._gate[sensorKey] = g._gate[sensorKey] || { lastMode: 'normal', lastHighAt: 0, lastNormalAt: 0 };
g._lastValid[sensorKey] = g._lastValid[sensorKey] || { temp: null, hum: null };

// --- helpers ---
const hasNum = v => Number.isFinite(Number(v));
const pick   = (cur, last) => (hasNum(cur) ? Number(cur) : (hasNum(last) ? last : null));
const f1     = v => (hasNum(v) ? Number(v).toFixed(1) : '‚Äî');

// --- last valid ---
if (hasNum(item.temp)) g._lastValid[sensorKey].temp = Number(item.temp);
if (hasNum(item.hum))  g._lastValid[sensorKey].hum  = Number(item.hum);

// --- fallback ---
item.temp = pick(item.temp, g._lastValid[sensorKey].temp);
item.hum  = pick(item.hum,  g._lastValid[sensorKey].hum);
item.value_source = (hasNum($json.temp) || hasNum($json.hum)) ? 'current' : 'last';

// --- threshold & mode ---
const thresholdTemp = hasNum(item.threshold_temp)
  ? Number(item.threshold_temp)
  : (hasNum($env.TEMP_THRESHOLD) ? Number($env.TEMP_THRESHOLD) : 35);

let modeNow = (item.mode === 'high' || item.mode === 'normal') ? item.mode : 'normal';
if (hasNum(item.temp)) modeNow = (Number(item.temp) > thresholdTemp) ? 'high' : 'normal';

const prevMode = g._gate[sensorKey].lastMode;

// --- decide frequency (log & notify) ---
let shouldLog = false;
let shouldNotify = false;
let status_detail = 'normal_periodic';

if (modeNow === 'high') {
  const due = nowMs >= g._gate[sensorKey].lastHighAt + HIGH_EVERY_MIN * 60 * 1000;
  if (due) {
    shouldLog = true;
    shouldNotify = true;
    g._gate[sensorKey].lastHighAt = nowMs;
  }
  status_detail = 'high';
} else {
  if (prevMode === 'high') {
    // recovery: ‡πÅ‡∏à‡πâ‡∏á & ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏ó‡∏±‡∏ô‡∏ó‡∏µ 1 ‡∏Ñ‡∏£‡∏±‡πâ‡∏á
    shouldLog = true;
    shouldNotify = true;
    status_detail = 'recovery';
    g._gate[sensorKey].lastNormalAt = nowMs;
  } else {
    const due = nowMs >= g._gate[sensorKey].lastNormalAt + NORMAL_EVERY_MIN * 60 * 1000;
    if (due) {
      shouldLog = true;
      shouldNotify = true;
      g._gate[sensorKey].lastNormalAt = nowMs;
    }
    status_detail = 'normal_periodic';
  }
}
g._gate[sensorKey].lastMode = modeNow;

// --- build Telegram text (plain text) ---
const statusCategory =
  status_detail === 'high'     ? 'high' :
  status_detail === 'recovery' ? 'recovery' : 'normal';

const ICONS = { high: 'üî•', recovery: 'üü¢', normal: '‚úÖ' };
const STATUS_TEXT = { high: '‡∏™‡∏π‡∏á‡πÄ‡∏Å‡∏¥‡∏ô‡πÄ‡∏Å‡∏ì‡∏ë‡πå', recovery: '‡∏ü‡∏∑‡πâ‡∏ô‡∏ï‡∏±‡∏ß', normal: '‡∏õ‡∏Å‡∏ï‡∏¥' };

const tUnit = item.tunit || '¬∞C';
const hUnit = item.hunit || '%';
const thText = hasNum(thresholdTemp) ? ` (TH ${thresholdTemp}${tUnit})` : '';

const lines = [
  `${ICONS[statusCategory]} DHT @ ${siteName}`,
  `üå°Ô∏è Temperature: ${f1(item.temp)} ${tUnit}${thText}`,
  `üíß Humidity: ${f1(item.hum)} ${hUnit}`,
  `üìü Status: ${STATUS_TEXT[statusCategory]} (${statusCategory}) | üïí Time: ${item.ts}`,
];
item.text = lines.join('\n');

// --- outputs ---
item.mode           = modeNow;
item.threshold_temp = thresholdTemp;
item.status_detail  = status_detail;
item.shouldLog      = !!shouldLog;     // ‡πÉ‡∏ä‡πâ‡∏Å‡∏±‡∏ö IF ‚Üí Sheets
item.shouldNotify   = !!shouldNotify;  // ‡πÉ‡∏ä‡πâ‡∏Å‡∏±‡∏ö IF ‚Üí Telegram

return [item];

// Parse DHT (normalize) ‚Äî DHT only
const SITE          = $json.site ?? $json.site_name ?? $env.SITE_NAME ?? "MSU_Lab";
const BASE_TOPIC    = $json.baseTopic ?? $json.topic ?? $env.MQTT_BASE_TOPIC ?? "DHTsensor";
// ‚¨áÔ∏è ‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô‡∏ö‡∏£‡∏£‡∏ó‡∏±‡∏î‡∏ô‡∏µ‡πâ
const INPUT_THRESHOLD = Number($json.threshold_temp ?? $env.TEMP_THRESHOLD ?? 35);

const TZ_OFFSET_MIN = Number($env.TZ_OFFSET_MIN ?? 420);

// ‡∏£‡∏±‡∏ö payload...
let payload = $json.message ?? $json;
if (typeof payload === 'string') { try { payload = JSON.parse(payload); } catch { payload = {}; } }

const toNum = (v) => {
  if (v === undefined || v === null) return null;
  if (typeof v === 'string' && v.trim() === '') return null;
  const n = Number(v);
  return Number.isFinite(n) ? n : null;
};

const temp = toNum(payload.temp ?? payload.temperature ?? payload.dht?.temp ?? payload.DHT11?.temp ?? payload.DHT22?.temperature);
const hum  = toNum(payload.hum  ?? payload.humidity  ?? payload.dht?.hum  ?? payload.DHT11?.humidity ?? payload.DHT22?.hum);

// ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞‡∏à‡∏≤‡∏Å threshold ‡∏ó‡∏µ‡πà‡πÑ‡∏î‡πâ‡∏à‡∏≤‡∏Å Set node (‡∏´‡∏£‡∏∑‡∏≠ ENV)
const hasThreshold = Number.isFinite(INPUT_THRESHOLD);
const isHigh = hasThreshold && temp !== null && temp > INPUT_THRESHOLD;
const mode   = isHigh ? 'high' : 'normal';

const ts = new Date(Date.now() + Number($env.TZ_OFFSET_MIN ?? 420) * 60 * 1000)
  .toISOString().replace('T',' ').slice(0,19);

const status_detail = (() => {
  if (temp === null) return 'temp=N/A';
  if (!hasThreshold) return `TEMP ${temp}¬∞C (no TH)`;
  return isHigh
    ? `TEMP ${temp}¬∞C > TH ${INPUT_THRESHOLD}¬∞C`
    : `TEMP ${temp}¬∞C ‚â§ TH ${INPUT_THRESHOLD}¬∞C`;
})();

return [{
  ts,
  site: SITE,
  temp,  tunit: $json.tunit || '¬∞C',
  hum,   hunit: $json.hunit || '%',
  threshold_temp: hasThreshold ? INPUT_THRESHOLD : null,
  mode,
  isHigh: isHigh ? 1 : 0,
  isLow : (hasThreshold && temp !== null && temp < INPUT_THRESHOLD) ? 1 : 0,
  status_detail,
  baseTopic: BASE_TOPIC,
}];

// Summarize (target day) ‚Äî ‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö DHT/3in1, ‡∏™‡πà‡∏á count_low / count_recovery / count_normal ‡πÄ‡∏û‡∏¥‡πà‡∏°
// ‡∏Å‡∏£‡∏≠‡∏á‡πÄ‡∏â‡∏û‡∏≤‡∏∞ "‡∏ß‡∏±‡∏ô‡πÄ‡∏õ‡πâ‡∏≤‡∏´‡∏°‡∏≤‡∏¢" (ISO ‡∏Ñ.‡∏®. YYYY-MM-DD) ‡πÅ‡∏•‡∏∞‡∏™‡∏£‡∏∏‡∏õ‡∏ï‡πà‡∏≠ site

const TZ_OFFSET_MIN = Number($env.TZ_OFFSET_MIN ?? 420); // Asia/Bangkok = +420 ‡∏ô‡∏≤‡∏ó‡∏µ
const pad = (n) => String(n).padStart(2, '0');
const toNum = (v) => { const n = Number(v); return Number.isFinite(n) ? n : null; };
const f1 = (v, d=1) => (v==null ? '‚Äî' : Number(v).toFixed(d));

// --------- ‡∏´‡∏≤ target day ---------
const targetDate = ($json.target_date && /^\d{4}-\d{2}-\d{2}$/.test($json.target_date))
  ? $json.target_date
  : (() => {
      const yLocalMs = Date.now() + TZ_OFFSET_MIN*60*1000 - 24*60*60*1000;
      return new Date(yLocalMs).toISOString().slice(0,10); // yesterday in ISO (e.g., 2025-09-15)
    })();

// --------- ‡πÄ‡∏ï‡∏£‡∏µ‡∏¢‡∏°‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÄ‡∏Ç‡πâ‡∏≤ ---------
const rows = items.map(i => i.json || {}).filter(r => r.ts);
const dayRows = rows
  .filter(r => String(r.ts).slice(0,10) === targetDate)
  .sort((a,b) => String(a.ts).localeCompare(String(b.ts)));

if (!dayRows.length) {
  return [{
    json: {
      day: targetDate, site: '‚Äî',
      temp_avg: null, temp_min: null, temp_max: null,
      hum_avg: null,  hum_min: null,  hum_max: null,
      rows: 0,
      high_count: 0,
      count_low: 0, count_recovery: 0, count_normal: 0,
      first_ts: null, last_ts: null,
      text: `üóìÔ∏è Daily Summary ‚Äî ${targetDate}\n(‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏ô‡∏ß‡∏±‡∏ô‡∏î‡∏±‡∏á‡∏Å‡∏•‡πà‡∏≤‡∏ß)`
    }
  }];
}

// --------- ‡∏™‡∏£‡∏∏‡∏õ‡∏ï‡πà‡∏≠ site ---------
const groups = {}; // site -> accumulator
for (const r of dayRows) {
  const site = r.site || 'UnknownSite';
  const t = toNum(r.temp);
  const h = toNum(r.hum);

  if (!groups[site]) {
    groups[site] = {
      site, day: targetDate,
      tSum:0, tCnt:0, tMin:null, tMax:null,
      hSum:0, hCnt:0, hMin:null, hMax:null,
      rows:0,
      high_count:0,                    // ‡∏ô‡∏±‡∏ö high (‡πÄ‡∏ú‡∏∑‡πà‡∏≠‡πÉ‡∏ä‡πâ‡∏ï‡πà‡∏≠)
      count_low:0, count_recovery:0, count_normal:0, // <-- ‡πÄ‡∏û‡∏¥‡πà‡∏° 3 ‡∏ü‡∏¥‡∏•‡∏î‡πå
      firstTs:null, lastTs:null,
      _prevAbnormal:null               // 'high' | 'low' | null (‡πÑ‡∏ß‡πâ‡∏´‡∏≤ recovery ‡∏ñ‡πâ‡∏≤ log ‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏ö‡∏£‡∏£‡∏ó‡∏±‡∏î recovery)
    };
  }
  const g = groups[site];
  g.rows++;

  if (t !== null) {
    g.tSum += t; g.tCnt++;
    g.tMin = (g.tMin==null) ? t : Math.min(g.tMin, t);
    g.tMax = (g.tMax==null) ? t : Math.max(g.tMax, t);
  }
  if (h !== null) {
    g.hSum += h; g.hCnt++;
    g.hMin = (g.hMin==null) ? h : Math.min(g.hMin, h);
    g.hMax = (g.hMax==null) ? h : Math.max(g.hMax, h);
  }

  // ----- ‡∏à‡∏±‡∏î‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞ (robust: ‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö DHT/3in1) -----
  const sd = String(r.status_detail || '').toLowerCase();   // high | low | recovery | normal_periodic | ''
  const mode = String(r.mode || '').toLowerCase();          // high/normal ‡∏´‡∏£‡∏∑‡∏≠ low/normal (‡πÅ‡∏•‡πâ‡∏ß‡πÅ‡∏ï‡πà‡πÇ‡∏ü‡∏•‡∏ß‡πå)
  const isHighFlag = String(r.isHigh) === '1';
  const isLowFlag  = String(r.isLow)  === '1';

  // ‡πÄ‡∏á‡∏∑‡πà‡∏≠‡∏ô‡πÑ‡∏Ç‡∏≠‡∏ô‡∏∏‡∏û‡∏±‡∏ô‡∏ò‡πå (‡∏Å‡∏£‡∏ì‡∏µ‡πÑ‡∏°‡πà‡∏°‡∏µ status_detail)
  const tempThr = toNum(r.threshold_temp ?? r.threshold);
  const isHighByTemp = (t!=null && tempThr!=null) ? (t > tempThr) : false;

  const dist = toNum(r.distance);                           // ‡πÄ‡∏ú‡∏∑‡πà‡∏≠‡∏Å‡∏£‡∏ì‡∏µ 3in1
  const dThr = toNum(r.dthreshold ?? r.threshold);
  const isLowByDist = (dist!=null && dThr!=null) ? (dist <= dThr) : false;

  let cat;
  if (sd === 'recovery') cat = 'recovery';
  else if (sd === 'high') cat = 'high';
  else if (sd === 'low')  cat = 'low';
  else if (sd === 'normal_periodic' || sd === 'normal') cat = 'normal';
  else {
    // ‡πÄ‡∏î‡∏≤: ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö DHT ‡πÉ‡∏´‡πâ‡∏ñ‡∏∑‡∏≠‡∏ß‡πà‡∏≤ high/normal; ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö 3in1 ‡∏≠‡∏≤‡∏à‡πÄ‡∏Å‡∏¥‡∏î low/normal
    const isHigh = isHighFlag || mode === 'high' || isHighByTemp;
    const isLow  = isLowFlag  || mode === 'low'  || isLowByDist;
    if (isHigh) cat = 'high';
    else if (isLow) cat = 'low';
    else cat = 'normal';
  }

  // ----- ‡∏ô‡∏±‡∏ö‡∏™‡∏ñ‡∏¥‡∏ï‡∏¥‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£ -----
  if (cat === 'high') {
    g.high_count++;
    g._prevAbnormal = 'high';
  } else if (cat === 'low') {
    g.count_low++;
    g._prevAbnormal = 'low';
  } else if (cat === 'recovery') {
    g.count_recovery++;
    g._prevAbnormal = null;
    g.count_normal++;                 // recovery ‡∏Å‡πá‡∏ñ‡∏∑‡∏≠‡∏ß‡πà‡∏≤‡πÄ‡∏Ç‡πâ‡∏≤‡∏™‡∏π‡πà‡∏õ‡∏Å‡∏ï‡∏¥ 1 ‡∏Ñ‡∏£‡∏±‡πâ‡∏á
  } else { // normal
    g.count_normal++;
    // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏ö‡∏£‡∏£‡∏ó‡∏±‡∏î recovery ‡πÉ‡∏ô log ‡πÉ‡∏´‡πâ‡∏ï‡∏£‡∏ß‡∏à‡∏à‡∏±‡∏ö‡∏à‡∏≤‡∏Å‡∏Å‡∏≤‡∏£‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô‡πÅ‡∏õ‡∏•‡∏á abnormal -> normal
    if (g._prevAbnormal) {
      g.count_recovery++;
      g._prevAbnormal = null;
    }
  }

  // first/last
  const ts = String(r.ts);
  if (!g.firstTs || ts < g.firstTs) g.firstTs = ts;
  if (!g.lastTs  || ts > g.lastTs)  g.lastTs  = ts;
}

// --------- ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå‡∏ï‡πà‡∏≠ site ---------
const out = [];
for (const site of Object.keys(groups)) {
  const g = groups[site];
  const tAvg = g.tCnt ? g.tSum/g.tCnt : null;
  const hAvg = g.hCnt ? g.hSum/g.hCnt : null;

  const text = [
    `üóìÔ∏è Daily Summary ‚Äî ${g.day}`,
    `üìç Site: ${g.site}`,
    `üå°Ô∏è Temp: avg ${f1(tAvg)}¬∞C (min ${f1(g.tMin)} / max ${f1(g.tMax)})`,
    `üíß Hum : avg ${f1(hAvg)}%  (min ${f1(g.hMin)} / max ${f1(g.hMax)})`,
    `‚ö†Ô∏è High: ${g.high_count} | Low: ${g.count_low} | Recovery: ${g.count_recovery} | üßæ Rows: ${g.rows}`,
    `üïí First: ${g.firstTs || '‚Äî'} | Last: ${g.lastTs || '‚Äî'}`
  ].join('\n');

  out.push({ json: {
    day: g.day,
    site: g.site,
    rows: g.rows,
    // ‡∏™‡∏ñ‡∏¥‡∏ï‡∏¥ temp/hum
    temp_avg: tAvg, temp_min: g.tMin, temp_max: g.tMax,
    hum_avg: hAvg,  hum_min: g.hMin, hum_max: g.hMax,
    // ‡∏ô‡∏±‡∏ö‡πÄ‡∏´‡∏ï‡∏∏‡∏Å‡∏≤‡∏£‡∏ì‡πå
    high_count: g.high_count,
    count_low: g.count_low,
    count_recovery: g.count_recovery,
    count_normal: g.count_normal,
    // ‡πÄ‡∏ß‡∏•‡∏≤
    first_ts: g.firstTs, last_ts: g.lastTs,
    // ‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏°‡∏û‡∏£‡πâ‡∏≠‡∏°‡∏™‡πà‡∏á Telegram
    text
  }});
}

return out;
