// Summarize (target day) â€” XY-MD02 only (Temp/Hum)

const TZ_OFFSET_MIN = Number($json.tz_offset_min ?? 420);
const toNum = (v) => { const n = Number(v); return Number.isFinite(n) ? n : null; };
const f1 = (v, d=1) => (v==null ? 'â€”' : Number(v).toFixed(d));

const msPerMin = 60 * 1000;
const tzMs = TZ_OFFSET_MIN * msPerMin;

// helper for local time
const PLAIN_LOCAL = /^\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2}$/;
const toLocalDateStr = (d) => new Date(d.getTime() + tzMs).toISOString().slice(0,10);
const toLocalIso = (d) => new Date(d.getTime() + tzMs).toISOString().replace('T',' ').slice(0,19);

// parse ts robustly
const parseTs = (ts) => {
  if (ts == null) return null;
  if (typeof ts === 'number') return new Date(ts);
  if (typeof ts === 'string') {
    const s = ts.trim();
    if (PLAIN_LOCAL.test(s)) return new Date(s.replace(' ', 'T'));
    if (/^\d{10,13}$/.test(s)) return new Date(Number(s));
    const d = new Date(s);
    return isNaN(d.getTime()) ? null : d;
  }
  const d = new Date(ts);
  return isNaN(d.getTime()) ? null : d;
};

// --- Target day ---
const isTest = ($json.test_mode === true) || ($json.test_mode === 'true');
const targetDate = ($json.target_date && /^\d{4}-\d{2}-\d{2}$/.test($json.target_date))
  ? $json.target_date
  : (() => {
      const offsetMs = isTest ? 0 : -24*60*60*1000;
      const baseMs = Date.now() + tzMs + offsetMs;
      return new Date(baseMs).toISOString().slice(0,10);
    })();

// --- Input rows ---
const rows = items.map(i => i.json || {}).filter(r => r.ts != null);
const dayRows = rows
  .map(r => {
    const s = String(r.ts).trim();
    const d = parseTs(r.ts);
    if (!d) return null;
    const localDay = PLAIN_LOCAL.test(s) ? s.slice(0,10) : toLocalDateStr(d);
    return { r, d, ms: d.getTime(), s, localDay };
  })
  .filter(x => x && x.localDay === targetDate)
  .sort((a,b) => a.ms - b.ms);

if (!dayRows.length) {
  return [{
    json: {
      day: targetDate,
      site: 'â€”',
      temp_avg: null, temp_min: null, temp_max: null,
      hum_avg: null, hum_min: null, hum_max: null,
      rows: 0,
      high_count: 0, count_low: 0, count_recovery: 0, count_normal: 0,
      first_ts: null, last_ts: null,
      text: `ðŸ—“ï¸ Daily Summary â€” ${targetDate}\n(à¹„à¸¡à¹ˆà¸¡à¸µà¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¹ƒà¸™à¸§à¸±à¸™à¸”à¸±à¸‡à¸à¸¥à¹ˆà¸²à¸§)`
    }
  }];
}

// --- Group by site ---
const groups = {};
for (const { r, d, ms, s } of dayRows) {
  const site = (r.site || 'UnknownSite').toString();
  const t = toNum(r.temp);
  const h = toNum(r.hum);

  if (!groups[site]) {
    groups[site] = {
      site, day: targetDate,
      tSum:0, tCnt:0, tMin:null, tMax:null,
      hSum:0, hCnt:0, hMin:null, hMax:null,
      rows:0,
      high_count:0, count_low:0, count_recovery:0, count_normal:0,
      firstMs:null, lastMs:null, firstTs:null, lastTs:null,
      _prevWasHigh:false
    };
  }

  const g = groups[site];
  g.rows++;
  if (t!==null){g.tSum+=t;g.tCnt++;g.tMin=g.tMin==null?t:Math.min(g.tMin,t);g.tMax=g.tMax==null?t:Math.max(g.tMax,t);}
  if (h!==null){g.hSum+=h;g.hCnt++;g.hMin=g.hMin==null?h:Math.min(g.hMin,h);g.hMax=g.hMax==null?h:Math.max(g.hMax,h);}

  const sd = String(r.status_detail||'').toLowerCase();
  const mode = String(r.mode||'').toLowerCase();
  const isHighFlag = (r.isHigh===true)||(r.isHigh===1)||(String(r.isHigh)==='1');
  const thr = toNum(r.threshold_temp??r.threshold);
  const highByTemp = (t!=null&&thr!=null)?(t>thr):false;
  const cat = sd==='recovery'?'recovery':sd==='high'?'high':(sd==='normal'||sd==='normal_periodic'||sd==='init')?'normal':((isHighFlag||mode==='high'||highByTemp)?'high':'normal');

  if(cat==='high'){g.high_count++;g._prevWasHigh=true;}
  else if(cat==='recovery'){g.count_recovery++;g.count_normal++;g._prevWasHigh=false;}
  else{g.count_normal++;if(g._prevWasHigh){g.count_recovery++;g._prevWasHigh=false;}}

  if(g.firstMs==null||ms<g.firstMs){g.firstMs=ms;g.firstTs=PLAIN_LOCAL.test(s)?s:toLocalIso(d);}
  if(g.lastMs==null||ms>g.lastMs){g.lastMs=ms;g.lastTs=PLAIN_LOCAL.test(s)?s:toLocalIso(d);}
}

// --- Build outputs ---
return Object.values(groups).map(g=>{
  const tAvg=g.tCnt?g.tSum/g.tCnt:null;
  const hAvg=g.hCnt?g.hSum/g.hCnt:null;
  const text=[
    `ðŸ—“ï¸ Daily Summary â€” ${g.day}`,
    `ðŸ“ Site: ${g.site}`,
    `ðŸŒ¡ï¸ Temp: avg ${f1(tAvg)}Â°C (min ${f1(g.tMin)} / max ${f1(g.tMax)})`,
    `ðŸ’§ Hum : avg ${f1(hAvg)}%  (min ${f1(g.hMin)} / max ${f1(g.hMax)})`,
    `âš ï¸ High: ${g.high_count} | Recovery: ${g.count_recovery} | Normal: ${g.count_normal} | ðŸ§¾ Rows: ${g.rows}`,
    `ðŸ•’ First: ${g.firstTs||'â€”'} | Last: ${g.lastTs||'â€”'}`
  ].join('\n');

  return {json:{
    day:g.day,site:g.site,rows:g.rows,
    temp_avg:tAvg,temp_min:g.tMin,temp_max:g.tMax,
    hum_avg:hAvg,hum_min:g.hMin,hum_max:g.hMax,
    high_count:g.high_count,count_low:0,
    count_recovery:g.count_recovery,count_normal:g.count_normal,
    first_ts:g.firstTs,last_ts:g.lastTs,text
  }};
});

